function Painting({ painting }) {

  const { image, title } = painting;

  // JS Event Handler
  function sayHello() {
    return console.log("Hello");
  }

  return (
    <div>
      <img src={image} />
      <h4>
        "{title}" by {painting.artist.name}
      </h4>
      <p>Year: {painting.date}</p>
      <p>
        Dimensions: {painting.dimensions.width} in. x {painting.dimensions.height} in.
      </p>
      <button onClick={sayHello}>Add To Cart</button>
    </div>

  );
};

export default Painting;
