import { useState } from 'react';

function Button() {
    // Define an isToggled State to take over
    // the work of isAdded and isLiked

    const [ isToggled, setToggled ] = useState(false);

    return (
        <button onClick={() => setToggled(!isToggled)}>
          { isToggled ? "Remove From Cart" : "Add To Cart"}
          { isToggled ? "Liked" : "Like"}
        </button>
    );
}
