pre[class*="language-"] {
	color: var(--color-secondary);
	background: none;
	font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
	font-size: 0.8em;
	text-align: left;
	white-space: pre;
