/* Code blocks */
pre[class*="language-"] {
	position: relative;
	overflow: visible;
	padding: 0;
}

.token.comment,
.token.block-comment,
.token.prolog,
.token.doctype,
.token.cdata {
	color: #7F848E;
	font-style: italic;
}

.token.punctuation {
	color: var(--font-color-light);
}

.token.property,
.token.tag,
.token.boolean,
.token.number,
.token.function-name,
.token.constant,
.token.symbol,
.token.deleted {
	color: var(--font-color);
}

.token.function,
.token.builtin,
.token.inserted {
	color: var(--color-primary);
}

.token.selector,
.token.attr-name,
.token.string,
.token.char {
  color: var(--purple);
}

.token.operator,
.token.entity,
.token.url,
.token.variable {
	color: grey;
}

.token.atrule,
.token.attr-value,
.token.keyword,
.token.class-name {
	color: var(--pink);
}

.token.regex,
.token.important {
	color: #e90;
}

.language-css .token.string,
.style .token.string {
	color: #a67f59;
}

